css:
/* Linha da Borda Listagem de Produtos */

.glide * {
    box-sizing: inherit;
}
.product-card {
    border-radius: var(--border-radius);
    font-size: var(--font-size);
    height: 100%;
    padding: var(--spacer-2);
    position: relative;
    border: 1px solid #0000001f;
    border-radius: 0.375rem;
}
/* Linha da Borda Listagem de Produtos */

/* Linha da Borda Listagem de Produtos MEIO DO PRODUTO */
.product-card__pictures {
    height: 250px;
    border-bottom: 1px solid #00000036;
}
/* Linha da Borda Listagem de Produtos MEIO DO PRODUTO */


.info-bar {
    background-color: #ffffff00 !important;
    color: #000;
}

body {
  font-family: 'Poppins', sans-serif !important;
}

.product-card__buy.fade {
  opacity: 1 !important;
}


.product__buy button {
    width: 100%;
    background-color: #25BA34;
    border-color: #25BA34;
    position: relative;
    overflow: hidden;
}

.product__buy button::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 0;
    height: 100%;
    background-color: rgba(255, 255, 255, 0.3);
    opacity: 0;
    transition: width 0.3s ease-out, opacity 0.3s ease-out;
}

.product__buy button:hover::after {
    width: 120%;
    opacity: 1;
}

/* Botão Comprar Listagem Verde e Hover Verde Claro */
.btn-primary {
    background-color: #018937;
    border-color: #018937;
    border-radius: 0.375rem;
    text-transform: uppercase;
}
.btn-primary:focus, .btn-primary:hover {
   /* background-color: #0aab45;
    border-color: #0aab45; (ISSO É UM TESTE) */
    background-color: #0aab45;
    border-color: #018937;
    border-radius: 0.375rem;
    text-transform: uppercase;
}
/* Botão Comprar Listagem Verde e Hover Verde Claro */



.category-retail .product-card__name, .category-retail .product-card__prices, .collection-shelf .product-card__name, .collection-shelf .product-card__prices, .page–search .product-card__name, .page–search .product-card__prices {text-align: left;
}

.product-card__link {
text-decoration: none !important;
}

.rounded-icon {
    border: 1px solid #000000;
    border-radius: 50%;
    display: inline-block;
    flex: 0 0 3.25rem;
    font-size: 1.5rem;
    height: 3.25rem;
    line-height: 1;
    padding-top: 0.875rem;
    text-align: center;
    width: 3.25rem;
}

.products-carousel .products-carousel__item {
    padding: var(--spacer-1);
    width: 50%;
    background-color: #fff;
}

.products-carousel__title a, .products-carousel__title span {
    color: inherit;
    display: inline-block;
    padding-left: var(--spacer-3);
    padding-right: var(--spacer-3);
    position: relative;
}
