/* Cor texto Menu */
.header__nav a {
    position: relative;
    color: #000;
    text-decoration: none;
    overflow: hidden;
}

.header__nav a::before {
    content: "";
    position: absolute;
    bottom: 0;
    left: 0;
    width: 0;
    height: 1px;  /* Ajusta a altura do "laser" */
    background-color: #F0F0F0;
    transition: all 0.5s;
}

.header__nav a::after {
    content: attr(data-text);
    position: absolute;
    top: 0;
    left: 0;
    color: #fff;
    overflow: hidden;
    transition: all 0.5s;
    width: 100%;
    white-space: nowrap;
}

.header__nav a:hover::before {
    width: 100%;
    background-color: red;
}

.header__nav a:hover::after {
    color: red;
}
/* Cor texto Menu */


