css:
.rounded-icon {
    border: 1px solid #ff0023;
    border-radius: 50%;
    display: inline-block;
    flex: 0 0 3.25rem;
    font-size: 1.5rem;
    height: 3.25rem;
    line-height: 1;
    padding-top: 0.875rem;
    text-align: center;
    width: 3.25rem;
}
.info-bar {
    background: #343a4000;
    color: #ff0023;
    margin-left: auto;
    margin-right: auto;
    max-width: var(--content-max-width);
    width: 100%;
}

.header__buttons .btn .badge {
    font-size: .75rem;
    position: absolute;
    right: -3px;
    top: -2px;
    border-radius: 20px;
}

main .top-bar {
    display: grid;
    flex-direction: column-reverse;
}

.products-carousel .products-carousel__item {
    background: #fff;
}
.collection-shelf {
    background: #e8e8e8;
}



.products-carousel {
    background: #e8e8e8;
    margin: var(--spacer-4) auto;
    max-width: var(--content-max-width);
    min-height: 6rem;
    padding: 0 var(--spacer-2);
}

.products-carousel__title a, .products-carousel__title span {
    background: #e8e8e8;
    color: inherit;
    display: inline-block;
    padding-left: var(--spacer-3);
    padding-right: var(--spacer-3);
    position: relative;
}



.cookieconsent {
  background-color: #373737e8;
}
.cookieconsent .text::before {
  content: 'Cookies: a gente guarda estatísticas de visitas para melhorar sua experiência de navegação, saiba mais em nossa ';
  font-size: 13px;
}
.cookieconsent .text {
  color: #fff;
  font-size: 0px;
}
.cookieconsent .text a {
  font-size: 13px;
  color: #FCD000;
}

.cookieconsent button {
  color: #fff;
  border: 1px solid #fff;
}

@media (max-width: 991.98px) {
  .cookieconsent {
    top: 90px;
    bottom: auto;
  }
}
