/* Sombra Bordas Mega Menu */
.header__submenu {
    background: var(--light);
    border-top: 0px solid var(--gray);
    box-shadow: var(--box-shadow-sm);
    display: none;
    gap: var(--spacer-2);
    grid-template-columns: repeat(auto-fit,minmax(30%,1fr));
    margin-top: var(--spacer-4);
    max-width: 797px;
    min-width: 300px;
    overflow: auto;
    padding: var(--spacer-3);
    position: absolute;
    text-align: left;
    border-radius: 10px;
    box-shadow: 1px 1px 1px #000000;
    -webkit-box-shadow: 1px 1px 17px #565656;
  }
/* Sombra Bordas Mega Menu */



/* Estilo Mega Menu */
.header__submenu--full {
    left: 50%;
    max-height: 436px;
    max-width: 814px;
    overflow-y: auto;
    scrollbar-color: var(--secondary) var(--primary-yiq);
    scrollbar-width: thin;
    transform: translateX(-50%);
    width: 98%;
}

.header__submenu {
    background: var(--light);
    /* border-top: 0px solid var(--gray); */
    box-shadow: var(--box-shadow-sm);
    display: none;
    gap: var(--spacer-2);
    grid-template-columns: repeat(auto-fit,minmax(30%,1fr));
    margin-top: var(--spacer-4);
    max-width: 800px;
    min-width: 300px;
    overflow: auto;
    padding: var(--spacer-3);
    position: absolute;
    text-align: left;
    border-radius: 10px;
}
/* Estilo Mega Menu */



/* Linha Titulo Produto Listagem */
.products-carousel__title:before {
    background: #dee2e600;
    content: " ";
    height: var(--border-width);
    left: 10%;
    margin-top: -0.05rem;
    position: absolute;
    top: 50%;
    width: 80%;
}
/* Linha Titulo Produto Listagem a Esquerda */
.products-carousel__title {
    display: block;
    margin: 0;
    padding: var(--spacer-3);
    position: relative;
    text-align: left;
}

/* Linha Titulo Produto Listagem */


/* Borda Pesquisa Aredondada */
.header__search {
    overflow: hidden;
    position: relative;
    border-radius: 23px;
}
/* Borda Pesquisa Aredondada */

/* Teste */
.user-button {
  display: flex;
  flex-direction: column;
  align-items: left;
  justify-content: left;
  text-align: left;
}

.button-text {
  margin-top: 8px;
  font-size: 12px;
}

.button-subtext {
  margin-top: 4px;
  font-size: 12px;
  color: #888888;
}
/* Teste nome Login*/

/* Cor Icone Login Header */
main #user-button i:before {
    background-color: #c40000;
    content: "";
    display: inline-block;
    height: 36px;
    -webkit-mask: url(https://ecom.nyc3.digitaloceanspaces.com/storefront/clean/login.svg) no-repeat 50% 50%;
    mask: url(https://ecom.nyc3.digitaloceanspaces.com/storefront/clean/login.svg) no-repeat 50% 50%;
    -webkit-mask-size: cover;
    mask-size: cover;
    width: 36px;
}
/* Cor Icone Login Header */


/* Cor Icone Carrinho */
main #cart-button i:before {
    background-color: #c40000;
    content: "";
    display: inline-block;
    height: 36px;
    -webkit-mask: url(https://ecom.nyc3.digitaloceanspaces.com/storefront/clean/cart.svg) no-repeat 50% 50%;
    mask: url(https://ecom.nyc3.digitaloceanspaces.com/storefront/clean/cart.svg) no-repeat 50% 50%;
    -webkit-mask-size: cover;
    mask-size: cover;
    width: 36px;
}
/* Cor Icone Carrinho */





/* Quantidade de Produtos na Listagem */
@media screen and (min-width: 998px) {
    .product-item {
        max-width: 225px;
    }

    .glide__slide.products-carousel__item {
        width: 222px !important;
    }
}
/* Quantidade de Produtos na Listagem */

/* Cor texto Menu */
main .header__nav a {
    color: #000;
    text-decoration: none;
}
/* Cor texto Menu */


/* Header Cor de Fundo */
main .header {
    background-color: #b7b6b6;
    border-bottom: none;
}
/* Header Cor de Fundo */

.category-retail .product-card__name, .category-retail .product-card__prices, .collection-shelf .product-card__name, .collection-shelf .product-card__prices, .page–search .product-card__name, .page–search .product-card__prices {
    font-size: 13px;
    font-weight: 500;
    color: #333;
    margin-bottom: 10px;
}



.h1, h1 {
    font-size: 2.0rem;
}

/* Linha da Borda Listagem de Produtos */

.glide * {
    box-sizing: inherit;
}
.product-card {
    border-radius: var(--border-radius);
    font-size: var(--font-size);
    height: 100%;
    padding: var(--spacer-2);
    position: relative;
    border: 1px solid #0000001f;
    border-radius: 0.375rem;
}
/* Linha da Borda Listagem de Produtos */

/* Linha da Borda Listagem de Produtos MEIO DO PRODUTO */
.product-card__pictures {
    height: 250px;
    border-bottom: 1px solid #00000036;
}
/* Linha da Borda Listagem de Produtos MEIO DO PRODUTO */


.info-bar {
    background-color: #ffffff00 !important;
    color: #000;
}

body {
  font-family: "Proxima Nova",-apple-system,Roboto,Arial,sans-serif,sans-serif;
  font-weight: 300;
}

.product-card__buy.fade {
  opacity: 1 !important;
}


.product__buy button {
    width: 100%;
    background-color: #25BA34;
    border-color: #25BA34;
    position: relative;
    overflow: hidden;
}

.product__buy button::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 0;
    height: 100%;
    background-color: rgba(255, 255, 255, 0.3);
    opacity: 0;
    transition: width 0.3s ease-out, opacity 0.3s ease-out;
}

.product__buy button:hover::after {
    width: 120%;
    opacity: 1;
}

/* Icone carrinho Lista */
.product-card__buy button .i-shopping-bag::before {
content: url(/img/uploads/cesta-de-compras.png);
}
/* Icone carrinho Lista */


/* Icone carrinho Detalhes */
.fa-shopping-bag:before, .i-shopping-bag:before {
    content: "";
    content: url(/img/uploads/cesta-de-compras.png);
}
/* Icone carrinho Detalhes */


/* Botão Comprar Listagem Verde e Hover Verde Claro */
.btn-primary {
    background-color: #018937;
    border-color: #018937;
    border-radius: 0.375rem;
    text-transform: uppercase;
}
.btn-primary:focus, .btn-primary:hover {
   /* background-color: #0aab45;
    border-color: #0aab45; (ISSO É UM TESTE) */
    background-color: #0aab45;
    border-color: #018937;
    border-radius: 0.375rem;
    text-transform: uppercase;
}
/* Botão Comprar Listagem Verde e Hover Verde Claro */



.category-retail .product-card__name, .category-retail .product-card__prices, .collection-shelf .product-card__name, .collection-shelf .product-card__prices, .page–search .product-card__name, .page–search .product-card__prices {text-align: left;
}

.product-card__link {
text-decoration: none !important;
}

.rounded-icon {
    border: 1px solid #000000;
    border-radius: 50%;
    display: inline-block;
    flex: 0 0 3.25rem;
    font-size: 1.5rem;
    height: 3.25rem;
    line-height: 1;
    padding-top: 0.875rem;
    text-align: center;
    width: 3.25rem;
}

.products-carousel .products-carousel__item {
    padding: var(--spacer-1);
    width: 50%;
    background-color: #fff;
}

.products-carousel__title a, .products-carousel__title span {
    color: inherit;
    display: inline-block;
    padding-left: var(--spacer-3);
    padding-right: var(--spacer-3);
    position: relative;
}
