css:
.info-bar {
    background: #343a4000;
    color: #ff0023;
    margin-left: auto;
    margin-right: auto;
    max-width: var(--content-max-width);
    width: 100%;
}

.header__buttons .btn .badge {
    font-size: .75rem;
    position: absolute;
    right: -3px;
    top: -2px;
    border-radius: 20px;
}

main .top-bar {
    display: grid;
    flex-direction: column-reverse;
}
