css:
.info-bar {
    background-color: #ffffff00 !important;
    color: #000;
}

body {
  font-family: 'Roboto', sans-serif !important;
}

.product-card__buy.fade {
  opacity: 1 !important;
}


.product__buy button {
    width: 100%;
    background-color: #25BA34;
    border-color: #25BA34;
}



.category-retail .product-card__name, .category-retail .product-card__prices, .collection-shelf .product-card__name, .collection-shelf .product-card__prices, .page–search .product-card__name, .page–search .product-card__prices {text-align: left;
}

.product-card__link {
text-decoration: none !important;
}

.rounded-icon {
    border: 1px solid #000000;
    border-radius: 50%;
    display: inline-block;
    flex: 0 0 3.25rem;
    font-size: 1.5rem;
    height: 3.25rem;
    line-height: 1;
    padding-top: 0.875rem;
    text-align: center;
    width: 3.25rem;
}

.products-carousel .products-carousel__item {
    padding: var(--spacer-1);
    width: 50%;
    background-color: #fff;
}

.products-carousel__title a, .products-carousel__title span {
    color: inherit;
    display: inline-block;
    padding-left: var(--spacer-3);
    padding-right: var(--spacer-3);
    position: relative;
}
