options.buy:
.product__buy button {
    width: 200%;
    /* background-color: #25BA34; */
    border-color: #b3b3b340;
    position: relative;
    overflow: hidden;
}



.form-control {
    background-clip: padding-box;
    background-color: #efefef;
    border: 1px solid #fffffffa;
    border-radius: 5px;
    color: #000000;
    display: block;
    font-size: 1rem;
    font-weight: 400;
    height: calc(1.5em + 0.75rem + 2px);
    line-height: 1.5;
    padding: 0.375rem 0.75rem;
    transition: border-color .15s ease-in-out, box-shadow .15s ease-in-out;
    width: 100%;
}
